function StringBuilder(){this._ReturnValue=[];this.Append=function(a){this["_ReturnValue"].push((typeof a=="undefined"?"":a))};this.AppendLine=function(a,b){if(typeof b=="undefined"){this.Append(a)}else{this.AppendFormat(a,b)}this.Append("\r\n")};this.AppendFormat=function(c,d){var b=c;for(var a=0;a<d.length;a++){b=b.split("{"+a+"}").join(d[a])}this.Append(b)};this.ToString=function(){return this["_ReturnValue"].join("")};this.Clear=function(){this["_ReturnValue"]=[]};this.Replace=function(d,a){var c=this.ToString().join().split(d).join(a);this.Clear();this.Append(c)}};